/* eslint-disable no-unused-expressions */
import { useState, useEffect } from "react";
import { BackgroundButton } from "../../BackgroundButton/BackgroundButton";
import { TEXT_COLORS } from "../../../theme/theme";
import { Input } from "../../Atoms/Input";
import { getUserBalance } from "../../../Utils/walletInteract";
import { useDispatch, useSelector } from "react-redux";
import {
  isApproved,
  stake,
  withdraw,
  harvest,
  approve,
} from "../../../Utils/walletInteract";
import { ToastContainer, toast } from "react-toastify";
const GO_TO_DEPOSIT = true;
const GO_TO_WITHDRAW = false;

export const DepositWithdrawSection = ({
  isDepositSection = true,
  toggleDepositSection = () => {},
}) => {
  const [userBalance, setUserBalance] = useState(100);
  const text = isDepositSection ? "Deposit" : "Withdraw";
  const [approvalStatus, setApprovalStatus] = useState(false);
  const [inputAmout, setInputAmount] = useState("");
  const currentAddress = useSelector((state) => state.address);
  const dispatch = useDispatch();
  const loadApproveStatus = async () => {
    const _isApproved = await isApproved(currentAddress);
    setApprovalStatus(_isApproved);
    console.log(_isApproved);
  };
  const handleDepositClick = () => {
    if (approvalStatus) {
      if (inputAmout === 0) return;
      const transaction = toast.loading("Tx pending");
      stake(currentAddress, inputAmout)
        .then((res) => {
          toast.update(transaction, {
            render: "Succesfully staked",
            type: "success",
            isLoading: false,
            autoClose: 3000,
          });
        })
        .catch((error) => {
          if (error.code === 4001) {
            toast.update(transaction, {
              render:
                "MetaMask Tx Signature: User denied transaction signature.",
              type: "error",
              isLoading: false,
              autoClose: 3000,
            });
          } else {
            toast.update(transaction, {
              render: error.message || "Error",
              type: "error",
              isLoading: false,
              autoClose: 3000,
            });
          }
        });
    } else {
      const transaction = toast.loading("Transaction pending");
      approve(currentAddress)
        .then((res) => {
          toast.update(transaction, {
            render: "Successfully approved",
            type: "success",
            isLoading: false,
            autoClose: 3000,
          });
        })
        .catch((error) => {
          if (error.code === 4001) {
            toast.update(transaction, {
              render:
                "MetaMask Tx Signature: User denied transaction signature.",
              type: "error",
              isLoading: false,
              autoClose: 3000,
            });
          } else {
            toast.update(transaction, {
              render: error.message || "Error",
              type: "error",
              isLoading: false,
              autoClose: 3000,
            });
          }
        });
    }
  };
  const handleDepositAllClick = () => {
    if (userBalance === 0) return;
    const transaction = toast.loading("Transaction pending");
    stake(currentAddress, userBalance)
      .then((res) => {
        //load();
        toast.update(transaction, {
          render: "Successfully staked all",
          type: "success",
          isLoading: false,
          autoClose: 3000,
        });
      })
      .catch((error) => {
        if (error.code === 4001) {
          toast.update(transaction, {
            render: "MetaMask Tx Signature: User denied transaction signature.",
            type: "error",
            isLoading: false,
            autoClose: 3000,
          });
        } else {
          toast.update(transaction, {
            render: error.message || "Error",
            type: "error",
            isLoading: false,
            autoClose: 3000,
          });
        }
      });
  };
  const handleWithdrawClick = () => {
    console.log(`hi withdraw ${currentAddress},${userBalance}`);
  };
  const handleWithdrawAllClick = () => {
    console.log("hi withdrawall");
  };
  const handleHarvestClick = () => {
    console.log("hi harvest");
  };

  const loadUserData = async () => {
    const _balance = await getUserBalance(currentAddress);
    setUserBalance(_balance);
  };
  useEffect(() => {
    if (currentAddress !== "") {
      loadApproveStatus();
      loadUserData();
      console.log(`hi, ${currentAddress}`);
      console.log(`hi, ${userBalance}`);
    }
  }, [currentAddress]);
  return (
    <div className="flex-1">
      <div className="w-full h-full flex flex-col gap-3">
        <div className="flex gap-3">
          <BackgroundButton
            isGray={!isDepositSection}
            onClick={() => toggleDepositSection(GO_TO_DEPOSIT)}
          >
            Deposit
          </BackgroundButton>
          <BackgroundButton
            isGray={isDepositSection}
            onClick={() => toggleDepositSection(GO_TO_WITHDRAW)}
          >
            Withdraw
          </BackgroundButton>
        </div>
        <p className={`font-noto-sans-regular ${TEXT_COLORS.WHITE}`}>
          Balance:{" "}
          <span className={`font-noto-sans-bold ${TEXT_COLORS.PURPLE_DARK}`}>
            {userBalance} $ HOICHI
          </span>
        </p>
        <Input
          type="number"
          value={inputAmout}
          placeholder={`HOICHI ${text} Amount...`}
          onChange={setInputAmount}
        />
        <div className="flex justify-between gap-3">
          <BackgroundButton
            isGray={false}
            className="flex-1"
            onClick={() => {
              if (isDepositSection) {
                handleDepositClick();
              } else {
                handleWithdrawClick();
              }
            }}
          >
            {() => {
              if (isDepositSection) {
                approvalStatus ? "Deposit" : "Approve";
              } else {
                ("Withdraw");
              }
            }}
          </BackgroundButton>
          <BackgroundButton
            isGray={false}
            className="flex-1"
            onClick={() => {
              if (isDepositSection) {
                handleDepositAllClick();
              } else {
                handleWithdrawAllClick();
              }
            }}
          >
            {`${text} All`}
          </BackgroundButton>
          <BackgroundButton
            isGray={false}
            className="flex-1"
            onClick={handleHarvestClick}
          >
            Harvest
          </BackgroundButton>
        </div>
      </div>
      <ToastContainer
        className="text-white"
        position="top-right"
        autoClose={5000}
        hideProgressBar={false}
        newestOnTop={false}
        closeOnClick
        rtl={false}
        pauseOnFocusLoss
        draggable
        pauseOnHover
        theme="colored"
      />
    </div>
  );
};
